var link=document.querySelector(".feedback > .btn"),popup=document.querySelector(".feedback-popup"),overlay=document.querySelector(".modal-overlay"),close=popup.querySelector(".btn-exit"),login=popup.querySelector("[name=name]");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),popup.classList.add("modal-anim"),overlay.classList.add("modal-show"),login.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show"),popup.classList.remove("modal-anim"),overlay.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("modal-show")&&(popup.classList.remove("modal-show"),popup.classList.remove("modal-anim"),overlay.classList.remove("modal-show"))});